entry.value:
var flowFile = session.get();
if (flowFile != null) {
    try {

        var StreamCallback = Java.type("org.apache.nifi.processor.io.StreamCallback")
        var IOUtils = Java.type("org.apache.commons.io.IOUtils")
        var StandardCharsets = Java.type("java.nio.charset.StandardCharsets")
        flowFile = session.write(flowFile,
            new StreamCallback(function (inputStream, outputStream) {
                var text = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
                var out = JSON.parse(text);
				if(!Array.isArray(out))
					out = [out];
				out = out.map(function(items){
					if(!items.ProcessDimensionHeaders) return null;
					var keys = items.ProcessDimensionHeaders.split(',');
					var values = items.ProcessDimensionValues.split(',');
					var obj = {};
					for(var i=0;i<keys.length;i++)
						obj[keys[i]] = values[i];

					items.values = obj;
					delete items.ProcessDimensionHeaders;
					delete items.ProcessDimensionValues;
					return items;

				}
				).filter(function(itm){return itm != null});



                outputStream.write(new java.lang.String(JSON.stringify(out, null, '\t')).getBytes(StandardCharsets.UTF_8))
            }));

        session.transfer(flowFile, REL_SUCCESS);

    } catch (ex) {
        flowFile = session.putAttribute(flowFile, "processError", ex);
		log.error(ex);
        session.transfer(flowFile, REL_FAILURE);
    }
}
